import React from 'react';
import styled from 'styled-components';

// Styled components
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  padding-top: 50px;
`;

const Title = styled.h1`
  font-size: 3em;
  font-family: "Montserrat", sans-serif; /* Utilisation de font-family */
  color: #333; /* Couleur du texte */
  margin-bottom: 20px; /* Espacement après le titre */
`;

const Description = styled.p`
  font-size: 1.2em;
  color: #666; /* Couleur du texte */
  font-family: "Montserrat", sans-serif; /* Utilisation de font-family */
`;

const OurContacts = () => {
  const teamMembers = [
    {
      name: 'Jean Dupont',
      email: 'jean.dupont@leafai.com',
      phone: '+1234567890',
      photo: '/jean_dupont.jpg', // Chemin vers l'image de Jean Dupont
    },
    {
      name: 'Marie Durand',
      email: 'marie.durand@leafai.com',
      phone: '+0987654321',
      photo: '/marie_durand.jpg', // Chemin vers l'image de Marie Durand
    },
  ];

  const contactContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    paddingTop: '50px',
  };

  const contactStyle = {
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'column',
    width: '400px',
    marginBottom: '20px',
    border: '1px solid #ddd',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  };

  const imageStyle = {
    borderRadius: '50%',
    width: '200px',
    height: 'auto',
    marginBottom: '10px',
  };

  const infoStyle = {
    fontSize: '18px',
    margin: '10px 0',
  };

  const titleStyle = {
    textAlign: 'center',
    marginBottom: '10px', // Réduit la marge inférieure du titre
  };

  return (
    <Container>
      <div>
        <Title>Nos Contacts</Title> {/* Utilisation du composant Title */}
        <div style={contactContainerStyle}>
          <div>
            {teamMembers.map((member, index) => (
              <div key={index} style={contactStyle}>
                <img src={member.photo} alt={member.name} style={imageStyle} />
                <p style={{ fontWeight: 'bold', fontSize: '24px', margin: '0' }}>{member.name}</p>
                <p style={infoStyle}>Email: {member.email}</p>
                <p style={infoStyle}>Téléphone: {member.phone}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Description>
        Vous pouvez contacter notre équipe pour toute question, suggestion ou demande d'assistance. Nous sommes là pour vous aider !
      </Description>
    </Container>
  );
};

export default OurContacts;
