import React, { useState } from 'react';
import './SubmitPhotos.css';

const SubmitPhotos = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!selectedFile) {
      alert('Veuillez sélectionner une photo.');
      return;
    }

    setIsLoading(true);

    try {
      const formData = new FormData();
      formData.append('photo', selectedFile);

      const response = await fetch('/upload-photo', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Erreur lors de l\'envoi de la photo :', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ justifyContent: 'center', alignItems: 'center', textAlign: 'center', height: '79vh' }}>
      <h2>Soumettre une photo</h2>
      <div className="submit-container">
        <div className="submit-photo">
          {selectedFile && (
            <div>
              <h2>Photo sélectionnée :</h2>
              <img
                src={URL.createObjectURL(selectedFile)}
                alt="Photo sélectionnée"
                style={{ maxWidth: '100%', height: 'auto', minHeight: '50vh', maxHeight: '50vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}
              />
            </div>
          )}
        </div>
        <div className="submit-input">
          <label htmlFor="fileInput" className="bn31">
            <span className="bn31span">Choisir une photo</span>
            <input
              id="fileInput"
              type="file"
              style={{ display: 'none' }}
              onChange={handleFileChange}
            />
          </label>
        </div>
        <div className="submit-button">
          <button className="bn31" onClick={handleSubmit} disabled={!selectedFile || isLoading}>
            {isLoading ? 'Envoi en cours...' : 'Envoyer la photo'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default SubmitPhotos;
