import React, { useState } from 'react';

const SubmitPhotos = ({ footerHeight }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [maxImageHeight, setMaxImageHeight] = useState('70vh'); // Hauteur maximale par défaut

  // Calcule la hauteur maximale de l'image en fonction de la hauteur du footer
  useEffect(() => {
    const windowHeight = window.innerHeight;
    const maxHeight = windowHeight - footerHeight - 100; // 100 est une marge supplémentaire facultative
    setMaxImageHeight(maxHeight + 'px');
  }, [footerHeight]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async () => {
    // logique d'envoi de photo
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
      <div style={{ textAlign: 'center' }}>
        <h1>Submit Photos</h1>
        <input type="file" onChange={handleFileChange} />
        {selectedFile && (
          <div>
            <h2>Photo sélectionnée :</h2>
            <img
              src={URL.createObjectURL(selectedFile)}
              alt="Photo sélectionnée"
              style={{ maxWidth: '100%', height: 'auto', maxHeight: maxImageHeight }}
            />
          </div>
        )}
        <button onClick={handleSubmit} disabled={!selectedFile || isLoading}>
          {isLoading ? 'Envoi en cours...' : 'Envoyer la photo'}
        </button>
      </div>
    </div>
  );
};

export default SubmitPhotos;
